import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o as _,c as e,e as t}from"./app-60a1dd49.js";const o={},c=t("<p>《三命通会》卷 六·稼穑</p><p>赋云：戊己忻逢四季，乃为稼穑之名。</p><p>是戊己生逢季月，喜见木为官，止得一木为妙，木多则土虚，主虚诈为破家不仁之人。</p><p>辰未土聚之地，见巳午火即贵，亦不宜多，多则土燥，不能滋生万物。</p><p>丑戌之土，内怀金气，不宜重见，恐存煞气，不生万物，又不宜见金泄气，不贵。</p><p>秋土不成器为死土，因土内含金，冬土不成器为泥土，因土内含水，故土只四季也。</p><p>诗曰：</p><p>戊己日生宜四季，多防丑戌坏金气，生来见木或逢荧，个中消息真荣贵。</p><p>又：</p><p>戊己生逢四月中，戊辰丑未要全逢，喜行财地嫌官煞，运到东方定有凶。</p>",10),r=[c];function s(n,a){return _(),e("div",null,r)}const f=p(o,[["render",s],["__file","113卷 六·稼穑.html.vue"]]);export{f as default};
